package kapitel_2.aufgaben;

public class DreiecksTausch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 7;
		int b = 13;
		
		System.out.println("a = "+ a);
		System.out.println("b = "+ b);
		
		int c = a;
		a = b ;
		b = c;
		System.out.println("a = "+ a);
		System.out.println("b = "+ b);		

	}

}
