package kapitel_2.aufgaben;

public class Konstante {
	
	final static double PI = 3.1415926535897932384626433832795;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int radius = 5;
		double Flaeche = PI * radius * radius ;
		System.out.println("PI lautet: "+ PI);
		System.out.println("Der Kreis hat eine Fläche von: "+ Flaeche );
		
		
	    

	}

}
